import React, { Component } from 'react';
import "./styles.css";
import WheelComponent from "react-wheel-of-prizes";
import { Col, Row, Container, Button, Modal, Table } from 'react-bootstrap';

class App extends React.Component {
  constructor(props) {
    super(props)
    let names = ["Jonas R", "Oliver", "Jesper", "Mathias", "Patrick", "Frederik", "Mikkel",
      "Sebastian", "Torben", "Adam", "Nikolai", "Rasmus", "Lindsey", " Christian",
      "Frost", "Morten", "Mr. Kristjansson",
      "Cat", "Maria", "Lotte", "Agnete", "Else", "Masha", "Jarl"]

    this.state = { names: names }

  }

  dynamicSort = (property) => {
    var sortOrder = 1;
    if (property[0] === "-") {
      sortOrder = -1;
      property = property.substr(1);
    }
    return function (a, b) {
      var result = (a[property] < b[property]) ? -1 : (a[property] > b[property]) ? 1 : 0;
      return result * sortOrder;
    }
  }


  shufflePeople = (namesInput) => {

    let people = []

    let names = [...namesInput]
    let seatNumbers = []

    let length = names.length;

    for (let i = 0; i < names.length; i++) {
      seatNumbers[i] = i + 1
    }

    for (let i = 0; i < length; i++) {

      let seatNumberIndex = Math.floor(Math.random() * seatNumbers.length)
      let nameIndex = Math.floor(Math.random() * names.length)

      let person = {
        name: names[nameIndex],
        seatNumber: seatNumbers[seatNumberIndex]
      }

      people.push(person)

      seatNumbers.splice(seatNumberIndex, 1)
      names.splice(nameIndex, 1)

    }

    people.sort(this.dynamicSort("seatNumber"))

    return people.map(p => {
      return (
        <tr key={p.name}>
          <td>{p.seatNumber}</td>
          <td>{p.name}</td>
        </tr>
      )
    })

  }

  render() {



    return (
      <div>

        <Row>
          <Col xs={2}>

            <Table striped bordered hover variant="dark">
              <thead>
                <tr>
                  {/* <th>Sæde nummer</th> */}
                  <th>Navn</th>
                </tr>
              </thead>
              <tbody>
                {this.shufflePeople(this.state.names)}
              </tbody>
            </Table>
          </Col>

          <Col xs={2}></Col>

          <Col xs={4}>
          <h1 style={{textAlign:"center"}}> Velkommen til</h1>
          <h1 style={{textAlign:"center"}}> <b>TEQUILA GAMES</b></h1>

            <Wheel

            />
          </Col>

          <Col xs={2}>
            <h1>Regler:</h1>
            <h2>Taber dit hold, skal du tage et shot, simpelt!</h2>
          </Col>

          <Col xs={2}></Col>


        </Row>


      </div>
    );
  }
}


class Wheel extends Component {
  constructor(props) {
    super(props)
  }


  onFinished = (winner) => {
    console.log(winner)
  }


  render() {

    const segments = ['Tøjkæde',
      'Agurkestafet',
      'Gæt en røv',
      'Blind-shot challenge',
      'Quick math',
      'Det bedste rim',
      'Flip cup',
      'Beer pong shot',
      'Sejeste Yoga pose',
      'Flip cup mund']
    const seg_colors = [
      "#EE4040",
      "#F0CF50",
      "#815CD1",
      "#3DA5E0",
      "#34A24F",
      "#F9AA1F",
      "#EC3F3F",
      "#FF9000",
      "#34A24F",
      "#F0CF50",
      "#F9AA1F",
      "#EE4040",
      "#3DA5E0",
    ];

    return (
      <>


        <WheelComponent
          segments={segments}
          segColors={seg_colors}
          winningSegment='won 10'
          onFinished={(winner) => this.onFinished(winner)}
          primaryColor='black'
          contrastColor='white'
          buttonText='Spin'
          isOnlyOnce={false}
          size={290}
          upDuration={100}
          downDuration={1000}
          fontFamily='Arial'
        />

      </>

    )
  }
}




export default App;
