import React, { Component } from 'react';
import "./styles.css";
import WheelComponent from "react-wheel-of-prizes";
import { Col, Row, Container, Button, Modal, Table } from 'react-bootstrap';

class App extends React.Component {
  constructor(props) {
    super(props)

    const segments = [
      "better luck next time",
      "won 70",
      "won 10",
      "better luck next time",
      "won 2",
      "won uber pass"
    ];
    const segColors = ["#EE4040",
      "#F0CF50",
      "#815CD1",
      "#3DA5E0",
      "#34A24F",
      "#F9AA1F",
      "#EC3F3F",
      "#FF9000",
      "#34A24F",
      "#F0CF50",
      "#F9AA1F",
      "#EE4040",
      "#3DA5E0"];


  }


  dynamicSort = (property) => {
    var sortOrder = 1;
    if (property[0] === "-") {
      sortOrder = -1;
      property = property.substr(1);
    }
    return function (a, b) {
      var result = (a[property] < b[property]) ? -1 : (a[property] > b[property]) ? 1 : 0;
      return result * sortOrder;
    }
  }


  shufflePeople = (namesInput) => {

    let people = []

    let names = [...namesInput]
    let seatNumbers = []

    let length = names.length;

    for (let i = 0; i < names.length; i++) {
      seatNumbers[i] = i + 1
    }

    for (let i = 0; i < length; i++) {

      let seatNumberIndex = Math.floor(Math.random() * seatNumbers.length)
      let nameIndex = Math.floor(Math.random() * names.length)

      let person = {
        name: names[nameIndex],
        seatNumber: seatNumbers[seatNumberIndex]
      }

      people.push(person)

      seatNumbers.splice(seatNumberIndex, 1)
      names.splice(nameIndex, 1)

    }

    people.sort(this.dynamicSort("seatNumber"))

    return people.map(p => {
      return (
        <tr key={p.name}>
          <td>{p.seatNumber}</td>
          <td>{p.name}</td>
        </tr>
      )
    })

  }






  render() {


    return (

      <div className="App">
        <h1>Hello CodeSandbox</h1>
        <div>

        {/* <Wheel
              reset={this.state.resetWheel}
              names={this.state.names}
            /> */}

        </div>
        <h2>Start editing to see some magic happen!</h2>
      </div>


    );
  }
}


class Wheel extends Component {
  constructor(props) {
    super(props)

    const challenges = ['Tøjkæde',
      'Agurkestafet',
      'Gæt en røv',
      'Blind-shot challenge',
      'Quick math',
      'Det bedste rim',
      'Flip cup',
      'Beer pong shot',
      'Sejeste Yoga pose',
      'Flip cup mund']

  }


  onFinished = (winner) => {

      console.log(winner)
      }



  render() {


    const segments = this.state.challenges
    const seg_colors = [
      "#EE4040",
      "#F0CF50",
      "#815CD1",
      "#3DA5E0",
      "#34A24F",
      "#F9AA1F",
      "#EC3F3F",
      "#FF9000",
      "#34A24F",
      "#F0CF50",
      "#F9AA1F",
      "#EE4040",
      "#3DA5E0",
    ];

    return (
      <>


        <WheelComponent
          segments={segments}
          seg_colors={seg_colors}
          onFinished={(winner) => this.onFinished(winner)}
          primaryColor='black'
          contrastColor='white'
          buttonText='Spin' />

      </>

    )
  }
}




export default App;
