import React from "react";
import WheelComponent from "react-wheel-of-prizes";
import "./styles.css";

class App extends React.Component {
  constructor(props) {




    const segments = [
      "better luck next time",
      "won 70",
      "won 10",
      "better luck next time",
      "won 2",
      "won uber pass"
    ];
    const segColors = ["#EE4040",
      "#F0CF50",
      "#815CD1",
      "#3DA5E0",
      "#34A24F",
      "#F9AA1F",
      "#EC3F3F",
      "#FF9000",
      "#34A24F",
      "#F0CF50",
      "#F9AA1F",
      "#EE4040",
      "#3DA5E0"];





  }

  render() {



    return (


      <div className="App">
        <h1>Hello CodeSandbox</h1>
        <div>
          <WheelComponent
            segments={this.segments}
            segColors={this.segColors}
            onFinished={(winner) => this.onFinished(winner)}
            primaryColor="black"
            contrastColor="white"
            buttonText="Spin"
            isOnlyOnce={false}
            size={190}
            upDuration={500}
            downDuration={600}
            fontFamily="Arial"
          />
        </div>
        <h2>Start editing to see some magic happen!</h2>
      </div>


    );
  }
}

class Wheel extends Comment{
  constructor(props) {
    super(props)
  }

  onFinished = (winner) => {
    console.log(winner);
  }

  render() {

    return (
      <>
  <WheelComponent
            segments={this.segments}
            segColors={this.segColors}
            onFinished={(winner) => this.onFinished(winner)}
            primaryColor="black"
            contrastColor="white"
            buttonText="Spin"
            isOnlyOnce={false}
            size={190}
            upDuration={500}
            downDuration={600}
            fontFamily="Arial"
          />
          </>
          )
  }
}
