import React, { Component } from 'react';
import "./styles.css";
import WheelComponent from "react-wheel-of-prizes";
import { Col, Row, Container, Button, Modal, Table } from 'react-bootstrap';

class App extends React.Component {
  constructor(props) {
    super(props)
    let names = ["Jonas R", "Oliver", "Jesper", "Mathias", "Patrick", "Frederik", "Mikkel",
      "Sebastian", "Torben", "Adam", "Nikolai", "Rasmus", "Lindsey", " Christian",
      "Frost", "Morten", "Mr. Kristjansson",
      "Cat", "Maria", "Lotte", "Agnete", "Else", "Masha", "Jarl"]

    this.state = { names: names }

  }

  dynamicSort = (property) => {
    var sortOrder = 1;
    if (property[0] === "-") {
      sortOrder = -1;
      property = property.substr(1);
    }
    return function (a, b) {
      var result = (a[property] < b[property]) ? -1 : (a[property] > b[property]) ? 1 : 0;
      return result * sortOrder;
    }
  }


  shufflePeople = (namesInput) => {

    let people = []

    let names = [...namesInput]
    let seatNumbers = []

    let length = names.length;

    for (let i = 0; i < names.length; i++) {
      seatNumbers[i] = i + 1
    }

    for (let i = 0; i < length; i++) {

      let seatNumberIndex = Math.floor(Math.random() * seatNumbers.length)
      let nameIndex = Math.floor(Math.random() * names.length)

      let person = {
        name: names[nameIndex],
        seatNumber: seatNumbers[seatNumberIndex]
      }

      people.push(person)

      seatNumbers.splice(seatNumberIndex, 1)
      names.splice(nameIndex, 1)

    }

    people.sort(this.dynamicSort("seatNumber"))

    return people.map(p => {
      return (
        <tr key={p.name}>
          <td>{p.seatNumber}</td>
          <td>{p.name}</td>
        </tr>
      )
    })

  }

  render() {



    return (
      <div>

        <Row>
          <Col xs={2}>

            <Table striped bordered hover variant="dark">
              <thead>
                <tr>
                  {/* <th>Sæde nummer</th> */}
                  <th>Navn</th>
                </tr>
              </thead>
              <tbody>
                {this.shufflePeople(this.state.names)}
              </tbody>
            </Table>
          </Col>

          <Col xs={2}></Col>

          <Col xs={4}>
          <h1 style={{textAlign:"center"}}> Velkommen til</h1>
          <h1 style={{textAlign:"center"}}> <b>TEQUILA GAMES</b></h1>

            <Wheel

            />
          </Col>

          <Col xs={2}>
            <h1>Regler:</h1>
            <h2>Taber dit hold, skal du tage et shot, simpelt!</h2>
          </Col>

          <Col xs={2}></Col>


        </Row>


      </div>
    );
  }
}


class Wheel extends Component {
  constructor(props) {
    super(props)

    const challenges = ['Tøjkæde',
      'Agurkestafet',
      'Gæt en røv',
      'Blind-shot challenge',
      'Quick math',
      'Det bedste rim',
      'Flip cup',
      'Beer pong shot',
      'Sejeste Yoga pose',
      'Flip cup mund']



    this.state = { resetWheel: true, challenges, team: null, timer: 0 }

    setInterval(() => {
      if(this.state.timer > 0){
        this.setState({timer: this.state.timer-1})
      }
    }, 1000);

  }

  resetWheel = () => {
    this.setState({ resetWheel: !this.state.resetWheel })
  }

  onFinished = (winner) => {

    let teams = {
      'Tøjkæde': {
        hold1: 'Patrick, Mathias, Morten',
        hold2: 'Oliver, Sebastian, Frederik'
      }, 'Agurkestafet': {
        hold1: 'Cat, Agnete, Maria, Masha, Frederik, Else',
        hold2: 'Torben, Jesper, Jarl, Jonas R, Mr. Kristjansson, Adam'
      },  'Gæt en røv': {
        hold1: 'Sebastian, Agnete, Frost',
        hold2: 'Mathias, Jarl, Adam'
      }, 'Blind-shot challenge': {
        hold1: 'Masha, Christian, Torben',
        hold2: 'Rasmus, Agnete, Maria'
      }, 'Quick math': {
        hold1: 'Morten, Frost, Lotte, Agnete',
        hold2: 'Torben, Patrick, Mathias, Cat'
      }, 'Det bedste rim': {
        hold1: 'Oliver, Jonas R, Frederik, Mikkel',
        hold2: 'Cat, Agnete, Lotte, Mr. Kristjansson'
      }, 'Flip cup': {
        hold1: 'Adam, Rasmus, Nikolai, Mr. Kristjansson',
        hold2: 'Jarl, Maria, Cat, Mikkel'
      },  'Beer pong shot': {
        hold1: 'Masha, Patrick, Adam, Morten',
        hold2: 'Rasmus, Oliver, Frost, Else'
      }, 'Sejeste Yoga pose': {
        hold1: 'Lindsey, Christian, Frost',
        hold2: 'Rasmus, Masha, Jonas R'
      }, 'Flip cup mund': {
        hold1: 'Jonas R, Patrick, Cat, Adam, Morten, Maria',
        hold2: 'Rasmus, Nikolai, Agnete, Mathias, Sebastian, Jarl'
      }
    }

    for (let i = 0; i < this.state.challenges.length; i++) {
      if (this.state.challenges[i] == winner) {
        this.state.challenges.splice(i, 1)

      }

    }
    this.setState({ team: teams[winner], chosenChallenge: winner, timer: 10 })

  }

  render() {

    if (!this.state.resetWheel) {
      setTimeout(this.setState({ resetWheel: true }), 500);
    }

    if (!this.state.resetWheel) return (
      <Button onClick={() => this.resetWheel}> Reset me</Button>
    )

    const segments = this.state.challenges
    const seg_colors = [
      "#EE4040",
      "#F0CF50",
      "#815CD1",
      "#3DA5E0",
      "#34A24F",
      "#F9AA1F",
      "#EC3F3F",
      "#FF9000",
      "#34A24F",
      "#F0CF50",
      "#F9AA1F",
      "#EE4040",
      "#3DA5E0",
    ];

    return (
      <>


        <WheelComponent
          segments={segments}
          segColors={seg_colors}
          winningSegment='won 10'
          onFinished={(winner) => this.onFinished(winner)}
          primaryColor='black'
          contrastColor='white'
          buttonText='Spin'
          isOnlyOnce={false}
          size={290}
          upDuration={100}
          downDuration={1000}
          fontFamily='Arial'
        />

      </>

    )
  }
}




export default App;
